var utility = require('utility/utility');
function MapList(){
    var me = this;
    me._mapping = {};
    me._array = [];
}
utility.defineProperties(MapList.prototype, {
    put: function(key, item){
        var me = this,
            array = me._array,
            val = me._mapping[key];
        me._mapping[key] = item;
        if(val){
            array[array.indexOf(val)] = item;
        }else{
            array.push(item);
        }
    },
    
    remove: function(key){
        
    },
    
    get: function(ident){
        var me = this;
        return me._mapping[ident] || me._array[ident];
    },
    
    list: function(){
        return this._array;
    },
    
    clear: function(){
        var me = this;
        me._mapping = {};
        me._array = [];
    }
});
module.exports = MapList;