var utility = require('utility/utility'),
    MapList = require('MapList');
function Referer(key, options){
    var me = this,
        opts = me._options = utility.extend({
            title: 'no name'
        }, options || {});
    me._refererId = key;
    me._url = opts.url,
    me._startTime = opts.time,
    me._codeId = 'codeId_' + opts.ident,
    me._title = opts.title,
    
    
    me._codeList = [];
}

utility.defineProperties(Referer.prototype, {
    getRefererId: function(){
        return this._refererId;
    },
    
    getTitle: function(){
        return this._title;
    },
    
    addCode: function(codeId){
        var me = this,
            codeList = me._codeList;
        codeList.push(codeId);
    },
    
    getCodeList: function(){
        return this._codeList;
    }
});

module.exports = Referer;