var config = require('config');
function clientCode(){
    var _tracker_,
        _trackerStart_,
        _trackerEnd_,
        _trackerError_,
        _fnStart_,
        _fnEnd_;
    void function(){
        var conf = $;
        var socket = window._socket_ = io.connect('http://' + conf.socketServer.host + ':' + conf.socketServer.port),
            url = location.href.split( "#" )[0],
            title = document.title,
            keyTasks = [],
            tasks = [],
            limit = 2000,
            timer;
            
        socket.on('ready', onReady);
        socket.on('finish', function(data){
            if(window._taskId_){return;}//表示从phantomjs过来的，无需页面跳转
            var ident = data.ident,
                url = conf.socketServer.clientURL + '?page=' + ident;
            window.open(url, '_blank');
            socket.disconnect();
        });
        
        function onReady(data){
            if(!data.ready){return;}
            send();
        }
        
        function send(){
            var data;
            if(keyTasks.length){
                data = (data || []).concat(keyTasks);
                keyTasks = [];
            }
            if(tasks.length){
                data = (data || []).concat(tasks.splice(0, limit));
            }
//            data && console.log(data);
            data && socket.emit('send', data);
            clearTimeout(timer);
            timer = setTimeout(send, 500);
        }
        
        function onPageLoad(){
            setTimeout(function(){
                socket.emit('pageload', {taskId: window._taskId_});
            }, 1000);
        }
        
        if(window.addEventListener){
            window.addEventListener('load', onPageLoad, false);
        }else if(window.attachEvent){
            window.attachEvent('onload', onPageLoad);
        }
        
        _tracker_ = function(ident, arrivalId){
            tasks.push({
                type: 'tracker',
                ident: ident,
                arrivalId: arrivalId
            });
        };
        _trackerStart_ = function(ident){
            keyTasks.push({
                type: 'trackerStart',
                ident: ident,
                url: url,
                title: title || (title = document.title),
                time: new Date().getTime()
            });
        };
        _trackerEnd_ = function(ident){
            tasks.push({
                type: 'trackerEnd',
                ident: ident,
                time: new Date().getTime()
            });
        };
        _trackerError_ = function(ident, msg){
            tasks.push({
                type: 'trackerError',
                ident: ident,
                msg: msg
            });
        };
        _fnStart_ = function(){
            
        };
        
        _fnEnd_ = function(){
            
        };
    }();
}

exports.getCode = function(){
    var client = clientCode.toString(),
        conf = JSON.stringify(config, null, 4);
    return client.slice(client.indexOf('{') + 1, client.length -2)
        .replace('$', conf);
}